/* eslint-disable array-callback-return */
import React, { Component } from "react";
import TeamInfo from "./TeamInfo";

class App extends Component {
  constructor() {
    super();
    this.state = {
      teamMembers: TeamInfo,
      newArray: [
        {
          id: 4,
          name: "Ali",
          jobName: "React Native",
          email: "ali325@gmail.com",
          wesite: "",
        },
      ],
      numberOfMembers: 0,
    };
    this.Cards = this.Cards.bind(this);
  }
  Cards() {
    function addMember() {
      // this.setState({
      //   numberOfMembers: this.numberOfMembers + 1,
      //   newArray: this.newArray.push(this.teamMembers[0][this.numberOfMembers]),
      // });
      alert("fdjksl");
    }

    const members = this.state.newArray.map((ele) => {
      return (
        <div
          style={{
            border: ele.wesite ? "1px solid blue" : "1px solid red",
            width: "18rem",
          }}
          className="card p-3 m-2 col-6"
          key={ele.id}
        >
          <img
            src="https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg"
            className="card-img-top"
            alt=""
          />
          <div className="card-body">
            <div className="card-text">
              <p>Name: {ele.name}</p>
              <p>Job Name: {ele.jobName}</p>
              <p>Email: {ele.email}</p>
              <p>{ele.wesite}</p>
            </div>
          </div>
        </div>
      );
    });

    return (
      <div
        style={{
          width: "fit-content",
        }}
      >
        {members}
      </div>
    );
  }
  render() {
    let cards = this.Cards();
    return (
      <div className="container">
        <p className="bg-dark text-white pr-3 pl-3 pt-2 pb-2 text-center fs-1">
          My Team
        </p>
        <button
          className="btn btn-primary w-100"
          onClick={this.Cards.addMember}
        >
          Next Member
        </button>
        <div className="row d-flex ">{cards}</div>
      </div>
    );
  }
}

export default App;
