import React, { Component } from "react";

class Task extends Component {
  render() {
    document.addEventListener("click", (e) => {
      if (e.target.id == "card") {
        alert("Hi");
      }
    });
    return (
      <div
        id="card"
        style={{
          padding: "1px 5px 10px",
          backgroundColor:
            this.props.info.web === "" ? "red" : `rgb(75, 73, 73)`,
          color: "white",
          width: "calc(100% / 4 - 20px)",
          minHeight: "300px",
          textAlign: "center",
          display: "inline-block",
          margin: "10px",
        }}
      >
        <img
          src={this.props.info.img}
          style={{
            width: "100%",
            height: "150px",
            border: "1px solid white",
          }}
        />
        <h2 style={{ textTransform: "uppercase" }}> {this.props.name}</h2>
        <p style={{ textTransform: "uppercase" }}>{this.props.info.jobName}</p>
        <p>
          {this.props.info.email !== ""
            ? `Email: ${this.props.info.email}`
            : ""}
        </p>
        <p>
          {this.props.info.web !== "" ? `Website: ${this.props.info.web}` : ""}
        </p>
      </div>
    );
  }
}

export default Task;
