/* eslint-disable array-callback-return */
import React, { Component } from "react";
import TeamInfo from "./TeamInfo";

class App extends Component {
  constructor() {
    super();
    this.state = {
      teamMembers: TeamInfo,
    };
    this.Cards = this.Cards.bind(this);
  }
  Cards() {
    const mapping = this.state.teamMembers.map((ele) => {
      return (
        <div
          className="card border-primary p-3 m-2 col-6"
          style={{ width: "18rem" }}
          key={ele.id}
        >
          <img
            src="https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg"
            className="card-img-top"
            alt=""
          />
          <div className="card-body">
            <div className="card-text">
              <p>Name: {ele.name}</p>
              <p>Job Name: {ele.jobName}</p>
              <p>Email: {ele.email}</p>
              <p>{ele.wesite ? `Website: ${ele.wesite}` : `No Website`}</p>
            </div>
          </div>
        </div>
      );
    });
    return (
      <div
        style={{
          width: "fit-content",
        }}
      >
        {mapping}
      </div>
    );
    // console.log(this.state.teamMembers[0].name);
  }
  render() {
    let cards = this.Cards();
    return (
      <div className="container">
        <div className="row d-flex justify-content-center">
          {cards}
          {cards}
          {cards}
          {cards}
        </div>
      </div>
    );
  }
}

export default App;
