import styled from "styled-components";
const GlobalStyle = styled`
body {
  padding-top: 10px;
  background: linear-gradient(
    to right,
    hsl(200, 100%, 50%),
    hsl(175, 100%, 50%)
  );
}`;
export default GlobalStyle;
