import React, { Component } from "react";

class State extends Component {
  constructor() {
    super();
    this.state = {
      count: 0,
    };
  }
  render() {
    return (
      <div className="container">
        <h1>{this.state.count}</h1>
        <button className="btn btn-primary" onClick={this.state.count++}>
          Clikc me!
        </button>
      </div>
    );
  }
}

export default State;
