/* eslint-disable array-callback-return */
import React, { Component } from "react";
import TeamInfo from "./TeamInfo";

class App extends Component {
  constructor() {
    super();
    this.state = {
      teamMembers: TeamInfo,
    };
    this.Cards = this.Cards.bind(this);
  }
  Cards() {
    let mapping = this.state.teamMembers[0].map((ele) => {
      <div
        className="card border-primary p-3 m-2 col-6"
        style={{ width: "18rem" }}
      >
        <img
          src="https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg"
          className="card-img-top"
          alt=""
        />
        <div className="card-body">
          <div className="card-text">
            <p>Name: Abdulmalik</p>
            <p>Job Name: Front End</p>
            <p>Email: abdulmalikkld325@gmail.com</p>
            <p>Website: Abdulmalik-frontEnd.eb2a.com</p>
          </div>
        </div>
      </div>;
    });
    return <div className="row d-flex justify-content-center">{mapping}</div>;
    // console.log(this.state.teamMembers[0].name);
  }
  render() {
    let cards = this.Cards();
    return (
      <div className="container">
        {cards}
        {cards}
        {cards}
        {cards}
      </div>
    );
  }
}

export default App;
