import React from "react";

function Task(props) {
  return (
    <div
      style={{
        padding: "1px 5px 10px",
        backgroundColor: props.info.web === "" ? "red" : `rgb(75, 73, 73)`,
        color: "white",
        width: "calc(100% / 4 - 20px)",
        minHeight: "250px",
        textAlign: "center",
        display: "inline-block",
        margin: "10px",
      }}
    >
      <img
        src={props.info.img}
        style={{
          width: "100%",
          height: "150px",
          border: "1px solid white",
        }}
      />
      <div>
        <h2 style={{ textTransform: "uppercase" }}>{props.info.name}</h2>
        <p style={{ textTransform: "uppercase" }}>{props.info.jobName}</p>
        <p>{props.info.email !== "" ? `Email: ${props.info.email}` : ""}</p>
        <p>{props.info.web !== "" ? `Website: ${props.info.web}` : ""}</p>
      </div>
    </div>
  );
}

export default Task;
