/* eslint-disable array-callback-return */
import React, { Component } from "react";
import TeamInfo from "./TeamInfo";

class App extends Component {
  constructor() {
    super();
    this.state = {
      teamMembers: [...TeamInfo],
      newArray: [],
      numberOfMembers: 1,
    };

    this.Cards = this.Cards.bind(this);
    this.showMemeber = this.showMemeber.bind(this);
  }
  Cards() {
    const members = this.state.newArray.map((ele) => {
      return (
        <div
          style={{
            border: ele.wesite ? "1px solid blue" : "1px solid red",
          }}
          className="card p-3 m-2"
          key={ele.id}
        >
          <img
            src="https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg"
            className="card-img-top"
            alt=""
          />
          <div className="card-body">
            <div className="card-text">
              <p>Name: {ele.name}</p>
              <p>Job Name: {ele.jobName}</p>
              <p>Email: {ele.email}</p>
              <p>{ele.wesite}</p>
            </div>
          </div>
        </div>
      );
    });

    return (
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, 50%)",
        }}
      >
        {members}
      </div>
    );
  }

  showMemeber() {
    if (this.state.numberOfMembers <= 4) {
      this.setState({
        numberOfMembers: this.state.numberOfMembers + 1,
        newArray: this.state.teamMembers.slice(0, this.state.numberOfMembers),
      });
    } else {
      document.querySelector(".btn").classList.add("btn-secondary");
    }
  }

  render() {
    let cards = this.Cards();
    return (
      <div className="container">
        <p className="bg-dark text-white pr-3 pl-3 pt-2 pb-2 text-center fs-1">
          My Team
        </p>
        <button className="btn btn-primary w-100" onClick={this.showMemeber}>
          Next Member
        </button>
        <div
          style={{
            width: "100%",
          }}
        >
          {cards}
        </div>
      </div>
    );
  }
}

export default App;
